// TARS System Types - Autonomously generated by TARS itself
// This file represents TARS's understanding of its own data structures

export interface TarsStatus {
  online: boolean;
  version: string;
  uptime: number;
  agents: number;
  cuda: boolean;
  memoryUsage: number;
  cpuUsage: number;
}

export interface TarsProject {
  id: string;
  name: string;
  status: 'creating' | 'active' | 'completed' | 'error';
  created: string;
  description: string;
  files: string[];
}

export interface TarsAgent {
  id: string;
  name: string;
  persona: string;
  status: 'idle' | 'busy' | 'error';
  task?: string;
  capabilities: string[];
  performance: {
    tasksCompleted: number;
    successRate: number;
    averageExecutionTime: number;
  };
}

export interface TarsMetrics {
  cpu: number;
  memory: number;
  cuda_searches_per_sec: number;
  gpu_memory_used: number;
  gpu_memory_total: number;
  active_agents: number;
  total_projects: number;
}

export interface TarsCommand {
  id: string;
  command: string;
  status: 'pending' | 'running' | 'completed' | 'error';
  output?: string;
  timestamp: string;
}

export interface TarsMetascript {
  id: string;
  name: string;
  description: string;
  status: 'idle' | 'running' | 'completed' | 'error';
  lastRun?: string;
  executionTime?: number;
}
