let spacetimeDiagramSVG model =
    let width = 800.0
    let height = 600.0
    let centerX = width / 2.0
    let centerY = height / 2.0
    
    svg [
        Width (int width)
        Height (int height)
        Style [
            Border "2px solid #3498db"
            BorderRadius "8px"
            BackgroundColor "#f8f9fa"
            Display "block"
            Margin "0 auto"
        ]
    ] [
        // Time axis
        line [
            X1 50.0
            Y1 centerY
            X2 (width - 50.0)
            Y2 centerY
            Stroke "#2c3e50"
            StrokeWidth 2
        ] []
        
        // Space axis
        line [
            X1 centerX
            Y1 50.0
            X2 centerX
            Y2 (height - 50.0)
            Stroke "#2c3e50"
            StrokeWidth 2
        ] []
        
        // Positive time branch (expanding universe)
        path [
            D (sprintf "M %f %f Q %f %f %f %f" centerX centerY (centerX + 100.0) (centerY - 100.0) (width - 50.0) 100.0)
            Stroke "#e74c3c"
            StrokeWidth 3
            Fill "none"
        ] []
        
        // Negative time branch (contracting universe)
        path [
            D (sprintf "M %f %f Q %f %f %f %f" centerX centerY (centerX - 100.0) (centerY - 100.0) 50.0 100.0)
            Stroke "#9b59b6"
            StrokeWidth 3
            Fill "none"
        ] []
        
        // Symmetry point
        circle [
            Cx centerX
            Cy centerY
            R 8
            Fill "#27ae60"
            Stroke "#fff"
            StrokeWidth 2
        ] []
        
        // Labels
        text [
            X (centerX + 10.0)
            Y (centerY - 10.0)
            Fill "#2c3e50"
            FontSize "14px"
            FontWeight "bold"
        ] [ str "t = 0 (Big Bang)" ]
        
        text [
            X (width - 100.0)
            Y (centerY + 20.0)
            Fill "#2c3e50"
            FontSize "12px"
        ] [ str "Future" ]
        
        text [
            X 60.0
            Y (centerY + 20.0)
            Fill "#2c3e50"
            FontSize "12px"
        ] [ str "Past" ]
        
        text [
            X (centerX + 10.0)
            Y 30.0
            Fill "#2c3e50"
            FontSize "12px"
        ] [ str "Space" ]
    ]

